mapper.select:

select * from member m, FileUpload fu
where m.fu_no = fu.fu_no 
and m_no=#{m_no }

mapper.update:

		UPDATE member
  		SET m_pw = #{m_pw }
  		WHERE m_no = #{m_no }


		UPDATE member
  		SET m_nick = #{m_nick }
  		WHERE m_no = #{m_no }


		UPDATE member
  		SET fu_no = #{fu_no }
  		WHERE m_no = #{m_no }


		UPDATE member
  		SET m_valid = 'N'
  		WHERE m_no = #{m_no }
