mapper.select:
  		SELECT * FROM (
			WITH ratings AS(
    			SELECT 
			        p.p_no, p.p_price, p.p_name, f.fu_storedname, b.pb_name,
			        (select count(*) from productlike l where p.p_no = l.p_no) cntplike,
			        (select count(*) from productlike l where p.p_no = l.p_no and m_no = #{m_no }) plikecheck,


				        	OR p.po_no= #{p } 




			        		OR p.pb_no= #{p }




			        		OR p.ps_no= #{p }




			        		OR p.pca_no= #{p }


			        + 0.8*(CASE WHEN p.p_price BETWEEN #{p_lowprice } AND #{p_highprice } THEN 1 ELSE 0 END)
			        AS score
			    FROM product p
		        JOIN fileupload f
				ON p.fu_no = f.fu_no
		        JOIN productbrand b
		        ON p.pb_no = b.pb_no



			        		AND p.pco_no != #{p }




			        		AND p.pp_no != #{p }






			        		AND p.pco_no != #{p }




			        		AND p.pp_no != #{p }



			) 

			    SELECT * FROM ratings 
			    WHERE plikecheck=0
			    ORDER BY score DESC
			) WHERE ROWNUM <= 10
