mapper.select:
  		select * from ( 
  		select rownum rnum, B.* from(
  		select p.fu_no, f.fu_storedname from product p
		inner join fileupload f on f.fu_no = p.fu_no
  		where 1=1

  			and pca_no=#{pca_no}


  			and p_name like '%' || #{word} || '%'

  		) B	order by rnum 
  		) R where rnum between #{paging.startNo} and #{paging.endNo}

select count(*) from ( select p.fu_no, f.fu_storedname from product p inner join fileupload f on f.fu_no = p.fu_no where 1=1
and pca_no=#{pca_no}
and p_name like '%' || #{word} || '%'
)
